mapper.select:
select a.id as "id", a.sid as "sId", a.tid as "tId", a.device_id as "deviceId", a.car_id as "carId", a.status as "status" from device a where 1 = 1
and device_id = #{deviceId}
and sid = #{sId}
and tid = #{tId}
and car_id like concat('%',#{carId},'%')

select a.id as id, a.sid as sId, a.tid as tId, a.device_id as deviceId, a.car_id as carId, a.status as status from device a where 1=1
and a.id = #{id}
and a.car_id like concat('%',#{carId},'%')

mapper.update:
update device set
    status = #{status}
where id = #{id}
