mapper.select:
select a.id as id, a.order_id as orderId, a.carId as "car.id", c.cardId as "car.cardId", a.unit as "unit.id", u.name as "unit.name", a.member_id as "member.id", a.type as "type.id", t.name as "type.name", a.startmiles as startmiles, a.endmiles as endmiles, a.position as position, a.status as status, a.startdate as startdate, a.enddate as enddate from lease a left join cars c on c.id = a.carId left join unit u on u.id = a.unit left join type t on t.id = a.type where 1 = 1
and unit = #{unit.id}
and startdate = #{logmin}
and enddate = #{logmax}
and carId = #{car.id}

mapper.update:
update lease set
    status = #{status}
where id = #{id}
